import React from 'react'
import '../../src/editor.css';
function Editor(menuData) {
  var splits;
  return (
    <div className="editor">
     <h1>{menuData.menuData.title}</h1>
     {console.log(menuData)}
     {console.log(menuData.menuData.content)}
     <div className="content">
     {menuData.menuData.type === 'img' || menuData.menuData.type === 'video'?
     splits =  menuData.menuData.content.split('fakepath\\')      : null
}
       {menuData.menuData.type === 'img' || menuData.menuData.type === 'video'?
       
       <img src={menuData.menuData.content}></img>
       :<p>{menuData.menuData.content}</p>
       }

     </div>
    </div>
  );
}

export default Editor;
