import React from 'react'
import {useState} from 'react';
function Demomenu() {
  const [menu,setMenu] = useState([]);
  const [items,setItems] = useState({key: '', value: ''});

  let handleChange = (e) => {
    setItems({ ...items, value: e.target.value})
  }

  let handleClick = () => {
    var dummyid = 1;
    setItems({ ...items, key: dummyid});
    setMenu([ ...menu, items]);
    console.log(menu);
  }
  return (
    <div>
     <input type="text" onChange={handleChange} placeholder="enter name"></input>
     <button onClick={handleClick}>+</button>
     <ul>
    {menu,map(obj => (
      <li>{obj.key}</li>
    ))}
    </ul>

    <ul>
     {menu.map(obj => (
       <li>{obj.value}</li>
     ))}
     </ul>
    </div>
  );
}

export default Demomenu;
