import React from 'react'
import '../../src/menu.css';
import {v4 as uuid} from 'uuid';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowLeft, faPlus } from '@fortawesome/free-solid-svg-icons'
import { faExpand } from '@fortawesome/free-solid-svg-icons'
import {useState, useEffect} from 'react';
import {Modal} from 'react-bootstrap'

function Menu() {

  const [menu,setMenu] = useState([]);
  const [items,setItems] = useState({key: '', value: ''});
  const [submenu,setsubmenu] = useState([]);
  const [submenuitems,setSubmenuitems] = useState({key: '', value: '', type: '', content: ''})
  const [clickid,setClickid] = useState('');
  const [subclickid,setSubclickid] = useState('');
  const [visible,setVisible] = useState(false);
  const [select,setSelect] = useState('');
  const [text,setText] = useState('');
  const [file,setFile] = useState('');

  let showModal = () => {
    setVisible(true);
}

let handleSelect = () => {
  let type = document.getElementById('type').value;
  console.log(type);
  setSelect(type);
}

let handleText = (e) => {
  setText(e.target.value);
}

let handleFile = () => {
  var x = document.getElementById("myFile").value;
  setFile(x);
  console.log(x);
}

  let handleChange = (e) => {
    setItems({ ...items, value: e.target.value});
  }

  let handleChange2 = (e) => {
    setSubmenuitems({ ...submenuitems, value: e.target.value});
  }

  let handleClick = () => {
    setMenu([ ...menu, items]);
    console.log(menu);
  }

  let handleClick2 = (id) => {
   setSubclickid(id);
    setSubmenuitems({ ...submenuitems, key: id});
    setsubmenu([ ...submenu, submenuitems]);
    console.log(submenu);
    console.log(subclickid);
  }

let viewMore = (id) => {
  setClickid(id);
}

useEffect(() => {
  var id = uuid();
  setItems({ ...items, key: id});
}, [menu]);




  return (
    <div className="menu">
        <nav>
            <ul>
                <li><a href="#" id="all">All</a></li>
                <li><a href="#">Board</a></li>
                <li><a href="#">Graph</a></li>
                <li><a href="#">Recent</a></li>
            </ul>
        </nav>
        <div className="menucontainer">
          <input type="text" onChange={handleChange2}></input>
        <div className="addmenu">
        <input type="text" onChange={handleChange} placeholder="DFIN" className="menuinput"></input>
        <FontAwesomeIcon icon={faPlus} className="faplus" onClick={handleClick}/>
        <FontAwesomeIcon icon={faExpand} className="faexpand"/>
        <FontAwesomeIcon icon={faArrowLeft} className="faarrowleft"/>
        </div>
        <ul>
        {menu.map(obj => (
          <div className="container">
          <li className="eachrow"><a href="#" onClick={viewMore.bind(this, obj.key)}>{obj.value}</a></li> 
        {obj.key == subclickid? 
        submenu.map(obj2 => (
           subclickid == obj2.key ?
            <li className="value">{obj2.value}</li> : null 
        )): null }
          <FontAwesomeIcon icon={faPlus} className="faplus2" onClick={handleClick2.bind(this, obj.key), showModal}/>
        </div>
        ))}
        </ul>
        </div>


        <Modal show={visible}>
                            <Modal.Header>Create User</Modal.Header>
                            <Modal.Body>
                            Title<br></br>
                            <input type="text" onChange={handleChange2} class="form-control" aria-label="Default" aria-describedby="inputGroup-sizing-default" id="editfname"></input><br></br><br></br>
                            Type<br></br>
                            <select onChange={handleSelect} id="type">
                              <option value="text">Text</option>
                              <option value="img">Image</option>
                              <option value="Video">Video</option>
                            </select><br></br><br></br>
                            {select === 'text'?
                            <textarea id="w3review" name="w3review" rows="4" cols="50" onChange={handleText}></textarea>
                            :
                            <input type="file" id="myFile" name="filename" onChange={handleFile} id="myfile"></input>
        }
                            </Modal.Body>
                            <Modal.Footer>
                                <button>Submit</button>
                                <button>Close</button>
                            </Modal.Footer>

                        </Modal>



    </div>

    
    
  );
}

export default Menu;
