import React from 'react'
import {useState} from 'react';
function Demomenu() {
  const [menu,setMenu] = useState([]);
  const [items,setItems] = useState({key: '', value: ''});
  const [clickid,setClickid] = useState('');

  let handleChange = (e) => {
    setItems({ ...items, value: e.target.value})
  }

  let handleClick = () => {
    var dummyid = Math.floor(Math.random(1,9) * 10);
    setItems({ ...items, key: dummyid});
    setMenu([ ...menu, items]);
    console.log(menu);
  }

let viewMore = (id) => {
  setClickid(id);
}

  return (
    <div>
     <input type="text" onChange={handleChange} placeholder="enter name"></input>
     <button onClick={handleClick}>+</button>
     <ul>
    {menu.map(obj => (
      <li onClick={viewMore.bind(this, obj.key)}>{obj.key}</li>
    ))}
    </ul>
      <br></br><br></br>
      {clickid != ''?
    <ul>
     {menu.map(obj => (
       clickid == obj.key ?
       <li>{obj.value}</li>: null
     ))}
     </ul>: null }
    </div>
  );
}

export default Demomenu;
