import React from 'react'
import '../../src/editor.css';
import {useState} from 'react'

function Editor(menuData) {
  const [style,setStyle] = useState('');
  const [Size,setSize] = useState('');

let bold = () => {
  setStyle("bold");
  document.getElementById('header').style.fontStyle = 'normal';
}
let italic = () => {
setStyle("italic");
document.getElementById('header').style.fontStyle = 'italic';
}

let h1 = () => {
  setSize("h1");
  document.getElementById('header').style.fontSize = '2em';
}
let h2 = () => {
  setSize("h2");
  document.getElementById('header').style.fontSize = '1.5em';
}










let boldp = () => {
  document.execCommand('bold');
}
let italicp = () => {
  document.execCommand('italic');
}

let h1p = () => {
  document.execCommand('fontSize', false, '5em');
}
let h2p = () => {
  document.execCommand('fontSize', false, '4em');
}

/*
  let headingFormat = () => {
    style === 'italic'?
    document.getElementById('header').style.fontStyle = 'italic':
    document.getElementById('header').style.fontStyle = 'normal'
    Size === 'h1' ?
    document.getElementById('header').style.fontSize = '2em':
    document.getElementById('header').style.fontSize = ''
    Size === 'h2' ?
    document.getElementById('header').style.fontSize = '1.5em':
    document.getElementById('header').style.fontSize = ''
  }*/
  return (
    <div className="editor">
     <h1 id="header">{menuData.menuData.title}</h1>
     <div className="format">
     <button onClick={italic} className="btn">I</button>
     <button onClick={bold} className="btn">B</button>
     <button onClick={h1} className="btn">H1</button>
     <button onClick={h2} className="btn">H2</button>
     </div>
     {console.log(menuData)}
     {console.log(menuData.menuData.content)}
     <hr></hr>
     <div className="format">
     <button onClick={italicp} className="btn2">I</button>
     <button onClick={boldp} className="btn2">B</button>
     <button onClick={h1p} className="btn2">H1</button>
     <button onClick={h2p} className="btn2">H2</button>
     </div>
     <div className="content" contentEditable = {true}>

       {menuData.menuData.type === 'img' || menuData.menuData.type === 'video'?
       
       <img src={require('../../images/' + menuData.menuData.content).default} alt = "noimage" height={200} width={200}></img>
       :<p>{menuData.menuData.content}</p>
       }

     </div>
    </div>
  );
}

export default Editor;
