import React from 'react'
import '../../src/menu.css';
import {v4 as uuid} from 'uuid';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowLeft, faPlus } from '@fortawesome/free-solid-svg-icons'
import { faExpand } from '@fortawesome/free-solid-svg-icons'
import {useState, useEffect} from 'react';
import {Container, Modal} from 'react-bootstrap'
// viewmore button clcik not assigning id not the correct logi for editid or click id
//line 184 container condition
function Menu({setChildData}) {

  const [menu,setMenu] = useState([]);
  const [items,setItems] = useState({key: '', value: ''});
  const [submenu,setsubmenu] = useState([]);
  const [submenuitems,setSubmenuitems] = useState({key: '', value: '', type: '', content: ''})
  const [clickid,setClickid] = useState('');
  const [subclickid,setSubclickid] = useState('');
  const [visible,setVisible] = useState(false);
  const [select,setSelect] = useState('');
  const [text,setText] = useState('');
  const [file,setFile] = useState('');

  const [container,setContainer] = useState([]);
  const [containeritems,setContainerItems] = useState({id: '', idd: '', title: '', type: '', content: ''});
  const [addid,setAddid] = useState('');
  const [editid,setEditid] = useState('');
  const [marginval,setMarginval] = useState(0);
  const [mainid,setmainid] = useState('');
  const [addbutton,setAddbutton] = useState('');
  const [containid,setContainid] = useState('');
  const [type,setType] = useState('');
  const [addidd,setAddidd] = useState('');
let closeBtn = () => {
  var newid = uuid();
  setContainerItems({ ...containeritems, idd: newid});
  setContainid(newid);
}

let addsub = (ty) => {
 // setEditid(id);
  setType(ty);
  console.log(ty);
}

let handleChangetitle = (e) => {
  setContainerItems({ ...containeritems, title: e.target.value})
  console.log(e.target.value);
}

let handleChangetype = () => {
  let contenttype = document.getElementById('type').value;
  setContainerItems({ ...containeritems, type: contenttype});
  setSelect(contenttype);
}

useEffect(() => {
  var id = uuid();
  setContainerItems({ ...containeritems, id: id});//here
  
}, [container]);



let handleSubmit = () => {
  setContainer([ ...container, containeritems]);
  console.log(container);
  console.log(mainid);
  console.log(type);
  console.log(addidd);
  //addid == containid ?
 // setMarginval(prev => prev + 25);
  //setMarginval(prev => prev);
}

let handleTextcontent = (e) => {
  setContainerItems({ ...containeritems, content: e.target.value});
}

let handleFilecontent = () => {
  var filecontent = document.getElementById('myFile').value;
  var splits =  filecontent.split('fakepath\\')  
  setFile(filecontent);
  setContainerItems({ ...containeritems, content: splits[1]});
}

let addMore = (containerid, containeridd) => {
  setAddid(containerid);
  console.log(containerid);
  setContainerItems({ ...containeritems, id: containerid})///here
  //setContainerItems({ ...containeritems, id2: uuid()});
  //setContainerItems({ ...containeritems, idd: uuid()});
  setAddbutton('faplus2');
  setVisible(true);
  setAddidd(containeridd);
  console.log(containeridd);
  console.log(mainid);
  setmainid(containerid);

  //setType(dtype);
}
//else prev val , check when its setting the margin
useEffect(() => {
  setMarginval(prev => prev + 25);
  //else set prev val
}, [addid]);



let showModal = () => {
  setVisible(true);
  var newid = uuid();
  setContainerItems({ ...containeritems, idd: newid});
  setContainid(newid);
  console.log('modal1');
}


  

let handleSelect = () => {
  let contenttype = document.getElementById('type').value;
  console.log(contenttype);
  setSelect(contenttype);
  setSubmenuitems({ ...submenuitems, type: contenttype });
}

let handleText = (e) => {
  setText(e.target.value);
  setSubmenuitems({ ...submenuitems, content: e.target.value});
}

let handleFile = () => {
  var x = document.getElementById('myFile').value;
  setFile(x);
  console.log(x);
  setSubmenuitems({ ...submenuitems, content: x});
}

  let handleChange = (e) => {
    setItems({ ...items, value: e.target.value});
  }

  let handleChange2 = (e) => {
    setSubmenuitems({ ...submenuitems, value: e.target.value});
    console.log(e.target.value);
  }

  

  let handleClick = () => {
    setMenu([ ...menu, items]);
    console.log(menu);
  }

  /*let handleClick2 = (id) => {
    console.log('hey its clicked'); //check this function not calling on click
   setSubclickid(id);
    setSubmenuitems({ ...submenuitems, key: id});
    console.log(submenu);
    console.log(subclickid);
  }*/

  let handleClick3 = () => {
    setsubmenu([ ...submenu, submenuitems]);
    console.log(submenu);
    console.log(subclickid);
  }

let viewMore = (id) => {
  setClickid(id);
  setEditid(id);
  console.log(id); 
}

useEffect(() => {
  var id = uuid();
  setItems({ ...items, key: id});
}, [menu]);

useEffect(() => {
  console.log(editid);
}, [editid]);




  return (
    <div className="menu">
        <nav>
            <ul>
                <li><a href="#" id="all">All</a></li>
                <li><a href="#">Board</a></li>
                <li><a href="#">Graph</a></li>
                <li><a href="#">Recent</a></li>
            </ul>
        </nav>
        <div className="menucontainer">
        <div className="addmenu">
        <input type="text" onChange={handleChange} placeholder="DFIN" className="menuinput"></input>
        <FontAwesomeIcon icon={faPlus} className="faplus" onClick={showModal}/>
        <FontAwesomeIcon icon={faExpand} className="faexpand"/>
        <FontAwesomeIcon icon={faArrowLeft} className="faarrowleft"/>
        </div>

        {container.map(obj => (
          <ul>
            <li className="eachrow">{obj.title}</li>
            {obj.type === 'container'?
            <FontAwesomeIcon icon={faPlus} className="faplus2" onClick={addMore.bind(this, obj.id, obj.type)}/>
          : null
          }

          </ul>
        ))}
        
        </div>



        <Modal show={visible}>
                            <Modal.Header>Create User</Modal.Header>
                            <Modal.Body>
                            Title<br></br>
                            <input type="text" onChange={handleChange2, handleChangetitle} class="form-control" aria-label="Default" aria-describedby="inputGroup-sizing-default" id="editfname"></input><br></br><br></br>
                            Type<br></br>
                            <select onChange={handleSelect, handleChangetype} id="type" className="select">
                              <option value="container">Container</option>
                              <option value="text">Text</option>
                              <option value="img">Image</option>
                              <option value="video">Video</option>
                            </select><br></br><br></br>
                            {select === 'text'?
                            <textarea id="w3review" name="w3review" rows="4" cols="50" onChange={handleText, handleTextcontent}></textarea>
                            : select === 'img' || select === 'video'?
                            <input type="file" id="myFile" onChange={handleFile, handleFilecontent}></input>
                            : null
                          }





                            </Modal.Body>
                            <Modal.Footer>
                                <button onClick={handleClick3, handleSubmit}>Submit</button>
                                <button onClick={() => (setVisible(false), console.log(containid))}>Close</button>
                                <button onClick={closeBtn}>help</button>
                            </Modal.Footer>

                        </Modal>



    </div>

    
    
  );
}

export default Menu;
