import Header from './Components/Header'
import Menu from './Components/menu'
import Editor from './Components/editor'
import React, { useState, useEffect } from "react";
//import Demomenu from './Components/demomenu'
function App() {
  const [childData, setChildDataa] = useState({id: '', idd: '', title:'', type:'', content: ''});
  const [childData2, setChilddata2] = useState({});

  let setChildData = (data) => {
    setChildDataa({ ...childData, id: data.id});
    setChildDataa({ ...childData, idd: data.idd});
    setChildDataa({ ...childData, title: data.title});
    setChildDataa({ ...childData, type: data.type});
    setChildDataa({ ...childData, content: data.content});

  }

  useEffect(() => {
    setChilddata2(childData);
  }, [childData]);
  return (
    <div className="App">
      <Header />{childData.title}
      <Menu setChildData={setChildData}/>
      <Editor menuData={childData2}/>
      {console.log(childData)}
    </div>
  );
}

export default App;
