import React from 'react'
import '../../src/editor.css';
import {useState} from 'react'

function Editor(menuData) {
  const [style,setStyle] = useState('');
  comst [Size,setSize] = useState('');
  return (
    <div className="editor">
     <h1 style={style === 'italic' ?{fontStyle: 'italic'}: {fontStyle: 'normal'}}>{menuData.menuData.title}</h1>
     <div className="format">
     <button onClick={() => setStyle("italic")}>I</button>
     <button onClick={() => setStyle("bold")}>B</button>
     <button onClick={() => setSize("2em")}>H1</button>
     <button onClick={() => setSize("1.5em")}>H2</button>
     </div>
     {console.log(menuData)}
     {console.log(menuData.menuData.content)}
     <div className="content">

       {menuData.menuData.type === 'img' || menuData.menuData.type === 'video'?
       
       <img src={require('../../images/' + menuData.menuData.content).default} alt = "noimage" height={200} width={200}></img>
       :<p>{menuData.menuData.content}</p>
       }

     </div>
    </div>
  );
}

export default Editor;
